mapper.select:
SELECT board_num, writer, title, created_time, hit_cnt, comment_count FROM board_ex
where title like concat('%',#{keyword},'%') OR content like concat('%', #{keyword}, '%') OR writer like concat('%', #{keyword}, '%')
ORDER BY board_num DESC LIMIT #{startPage}, #{perPageNum}

mapper.update:
UPDATE board_ex b
SET b.comment_count = (SELECT COUNT (comment_num) FROM comment_ex WHERE board_num = #{boardNum})
WHERE b.board_num = #{boardNum}

UPDATE
    board_ex
SET title   = #{title},
    content = #{content}
WHERE board_num = #{boardNum}
