mapper.update:
UPDATE like_dislike_ex
SET like_btn = like_btn + 1
WHERE board_num = #{boardNum} AND user_id = #{userId}

UPDATE like_dislike_ex
SET like_btn = like_btn - 1
WHERE board_num = #{boardNum} AND user_id = #{userId}

mapper.select:
SELECT COUNT (*)
FROM like_dislike_ex
WHERE board_num = #{boardNum} AND user_id = #{userId}
